import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Check for token in localStorage
        const token = localStorage.getItem('token');
        if (token) {
            verifyToken(token);
        } else {
            setLoading(false);
        }
    }, []);

    // Set up axios interceptor to include the token in every request
    useEffect(() => {
        const interceptor = axios.interceptors.request.use(
            config => {
                const token = localStorage.getItem('token');
                if (token) {
                    config.headers.Authorization = `Bearer ${token}`;
                }
                return config;
            },
            error => Promise.reject(error)
        );

        return () => {
            axios.interceptors.request.eject(interceptor);
        };
    }, []);

    const verifyToken = async (token) => {
        try {
            const response = await axios.post('/api/auth/verify', { token });
            if (response.data.valid) {
                setUser(response.data.user);
            } else {
                localStorage.removeItem('token');
                toast.error("Session expired. Please login again.");
            }
        } catch (error) {
            localStorage.removeItem('token');
            setError(error.response?.data?.error || 'Authentication error');
        } finally {
            setLoading(false);
        }
    };

    const login = async (email, password) => {
        try {
            const response = await axios.post('/api/auth/login', { email, password });
            const { token } = response.data;
            localStorage.setItem('token', token);
            await verifyToken(token);
            toast.success('Logged in successfully!');
            return { success: true };
        } catch (error) {
            const errorMessage = error.response?.data?.error || 'Login failed';
            setError(errorMessage);
            toast.error(errorMessage);
            return { success: false, error: errorMessage };
        }
    };

    const register = async (email, password, name) => {
        try {
            const response = await axios.post('/api/auth/register', { email, password, name });
            const { token } = response.data;
            localStorage.setItem('token', token);
            await verifyToken(token);
            toast.success('Registration successful!');
            return { success: true };
        } catch (error) {
            const errorMessage = error.response?.data?.error || 'Registration failed';
            setError(errorMessage);
            toast.error(errorMessage);
            return { success: false, error: errorMessage };
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        setUser(null);
        toast.success('Logged out successfully');
    };

    const fetchUserProfile = async () => {
        try {
            const response = await axios.get('/api/user/profile');
            return response.data;
        } catch (error) {
            toast.error('Failed to fetch profile');
            throw error;
        }
    };

    const updatePreferences = async (preferences) => {
        try {
            const response = await axios.put('/api/user/preferences', preferences);
            toast.success('Preferences updated');
            return response.data;
        } catch (error) {
            toast.error('Failed to update preferences');
            throw error;
        }
    };

    const fetchRoutines = async () => {
        try {
            const response = await axios.get('/api/user/routines');
            return response.data.routines;
        } catch (error) {
            toast.error('Failed to fetch routines');
            throw error;
        }
    };

    const saveRoutine = async (routineData) => {
        try {
            const response = await axios.post('/api/user/routines', routineData);
            toast.success('Routine saved successfully');
            return response.data;
        } catch (error) {
            toast.error('Failed to save routine');
            throw error;
        }
    };

    return (
        <AuthContext.Provider value={{
            user,
            loading,
            error,
            login,
            register,
            logout,
            isAuthenticated: !!user,
            fetchUserProfile,
            updatePreferences,
            fetchRoutines,
            saveRoutine
        }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
